import { Button, Form, Input, Image } from "antd";
import ImageUploader from "react-images-upload";

const NewOrEditUserForm = ({
  handleOk,
  loading,
  fileList,
  setFileList,
  imgUrl,
  setImgUrl,
  detailId,
  form,
}) => {
  const onDrop = (pictures) => {
    if (pictures.length === 0) {
      setImgUrl("");
      setFileList([]);
    } else {
      const foto = pictures[0];
      const fotoUrl = URL.createObjectURL(foto);
      setImgUrl(fotoUrl);
      setFileList(pictures);
    }
  };

  return (
    <Form
      initialValues={{
        firstName: "",
        lastName: "",
        dni: "",
        userName: "",
        email: "",
        password: "",
        confirmPassword: "",
      }}
      form={form}
      onFinish={handleOk}
      layout="vertical"
    >
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Image
          width={350}
          height={220}
          style={{ objectFit: "cover" }}
          src={imgUrl}
          fallback="data:image/png;base64,"
        />
      </div>
      <Form.Item
        name="firstName"
        label="Nombre"
        rules={[{ required: true, message: "Te faltó completar el nombre!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name="lastName"
        label="Apellido"
        rules={[{ required: true, message: "Te faltó completar el apellido!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name="dni"
        label="DNI"
        rules={[{ required: true, message: "Te faltó completar el DNI!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name="userName"
        label="UseName"
        rules={[{ required: true, message: "Te faltó completar el UserName!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        name="email"
        label="E-mail"
        rules={[
          {
            type: "email",
            message: "E-mail inválido!",
          },
          {
            required: true,
            message: "Por favor ingrese un E-mail!",
          },
        ]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        name="password"
        label="Password"
        hasFeedback
        rules={
          detailId !== ""
            ? [{}]
            : [
                {
                  required: true,
                  message: "Por favor ingrese el password!",
                },
              ]
        }
      >
        <Input.Password />
      </Form.Item>
      <Form.Item
        name="confirmPassword"
        label="Confirm Password"
        dependencies={["password"]}
        hasFeedback
        rules={
          detailId !== ""
            ? [{}]
            : [
                {
                  required: true,
                  message: "Por favor ingrese el password!",
                },
                ({ getFieldValue }) => ({
                  validator(_, value) {
                    if (!value || getFieldValue("password") === value) {
                      return Promise.resolve();
                    }

                    return Promise.reject(
                      new Error("Los passwords no coinciden!")
                    );
                  },
                }),
              ]
        }
      >
        <Input.Password />
      </Form.Item>

      <Form.Item name="image">
        <ImageUploader
          withIcon={false}
          label={null}
          buttonText="Buscar imagen"
          buttonStyles={{
            background: "black",
            width: "100%",
            marginLeft: 20,
          }}
          fileContainerStyle={{
            padding: "0px 2px",
            margin: 0,
            flexDirection: "row",
          }}
          onChange={onDrop}
          imgExtension={[".jpg", ".png"]}
          maxFileSize={5242880}
          singleImage={true}
          withPreview={true}
        />
      </Form.Item>
      <Form.Item>
        <Button
          htmlType="submit"
          type="primary"
          block={true}
          loading={loading}
          disabled={fileList.length === 0 && imgUrl === "" ? true : false}
        >
          {loading ? "Uploading" : "Start Upload"}
        </Button>
      </Form.Item>
    </Form>
  );
};

export default NewOrEditUserForm;
